// api.js
import axios from 'axios'

const baseURL = 'http://localhost:3001' // Use a mesma porta que você configurou no servidor

export const buscarMenuItems = async () => {
  try {
    const response = await axios.get(`${baseURL}/menuItems`)
    return response.data
  } catch (error) {
    console.error('Erro ao carregar os itens do menu:', error)
    throw error
  }
}

export const buscarGrupoSubgrupo = async () => {
  try {
    const response = await axios.get(`${baseURL}/grupoSubgrupo`)
    return response.data
  } catch (error) {
    console.error('Erro ao carregar os grupos e subgrupos:', error)
    throw error
  }
}
